import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 80,
  },
  typography: {
    h2: 12,
  },
});

const MostrarDatos = () => {
  const classes = useStyles();

  const [canales, setCanales] = useState();

  //Funcion getData crea la llamada a la URL de ApiChannel

  const getData = async () => {
    const response = await fetch("http://localhost:5001/api/channels");
    const result = await response.json();
    setCanales(result);
    console.log(result);
  };

  //Utilizamos el metodo de useEffect para para hacer la llamada, y que se ejecute una sola vez
  useEffect(() => {
    getData();
  }, []);

  return (
    //Aqui se crea la card de cada canal
    <div>
      <h1>Listado de canales</h1>
      <br />

      {!canales
        ? "Cargando data..."
        : canales.map((canal, index) => {
            return (
              <Grid spacing={3}>
                <Card key={canal.id} className={classes.root}>
                  <CardActionArea>
                    <CardContent>
                      <Typography variant="h2" className="titulo__cabecera">
                        {canal.nombre}
                      </Typography>
                    </CardContent>
                    <Typography className="numero__canal">
                      {canal.numero}
                    </Typography>
                    <div class="picture">
                        <img src={canal.imagen} alt={canal.nombre}></img>

                    </div>

                    <CardContent>Se√±al de {canal.tipo}</CardContent>
                    <CardActions>
                      <Button size="small" color="primary">
                        Detalle
                      </Button>
                    </CardActions>
                  </CardActionArea>
                </Card>
              </Grid>
            );
          })}
    </div>
  );
};

export default MostrarDatos;
