import React, { useState, useEffect } from "react";

const MostrarDatos = () => {

  const [canales, setCanales] = useState();

  //Funcion getData crea la llamada a la URL de ApiChannel

  const getData = async () => {
    const response = await fetch("http://localhost:5001/api/channels");
    const result = await response.json();
    setCanales(result);
    console.log(result)     
}

//Utilizamos el metodo de useEffect para para hacer la llamada, y que se ejecute una sola vez
    useEffect(() => {
      getData()
    },[]);

  return (
    //Aqui se crea la card de cada canal
    <div>
        <h1>Listado</h1>

        {!canales
         ? 
         'Cargando data...'
         :
        canales.map((canal, index) =>{
            return (<div key={index} class="card-principal">
            <div className="card-header">
            <h2 className="titulo__cabecera">{canal.nombre}</h2>
            </div><p class="numero__canal">{canal.numero}</p>
            <div class="picture">
            <img src={canal.imagen} alt="logo TVN de Chile"></img>
            </div>
            <div class="cardFooter">Se√±al de {canal.tipo}</div>
            </div>)
        })
        }

    </div>
  );
}


export default MostrarDatos;
