import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
("@material-ui/core");

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 140,
  },
});

const MostrarDatos = () => {
  const classes = useStyles();

  const [canales, setCanales] = useState();

  //Funcion getData crea la llamada a la URL de ApiChannel

  const getData = async () => {
    const response = await fetch("http://localhost:5001/api/channels");
    const result = await response.json();
    setCanales(result);
    console.log(result);
  };

  //Utilizamos el metodo de useEffect para para hacer la llamada, y que se ejecute una sola vez
  useEffect(() => {
    getData();
  }, []);

  return (
    //Aqui se crea la card de cada canal
    <div>
      <h1>Listado de canales</h1>
      <br />

      {!canales
        ? "Cargando data..."
        : canales.map((canal, index) => {
            return (
              <Card key={canal.id} className={classes.root}>
                <CardActionArea>
                  <container className="card-header">
                    <Typography variant="h2" className="titulo__cabecera">
                      {canal.nombre}
                    </Typography>
                  </container>
                  <Typography className="numero__canal">
                    {canal.numero}
                  </Typography>
                  <CardMedia className={classes.media}
                  image src={canal.imagen} alt={canal.nombre}
                  />
                  <CardContent>
                    Se√±al de {canal.tipo}
                  </CardContent>
                  <CardActions>
                  <Button size="small" color="primary">Detalle</Button>
                  </CardActions>
                </CardActionArea>
              </Card>
            );
          })}
    </div>
  );
};

export default MostrarDatos;
